import { CortiClient } from "../../src/Client";
import { mockServerPool } from "../mock-server/MockServerPool";
import { mockOAuth } from "./mockAuth";

describe("AuthClient", () => {
    test("getToken", async () => {
        const server = mockServerPool.createServer();
        mockOAuth(server);

        const client = new CortiClient({
            maxRetries: 0,
            clientId: "client_id_123",
            clientSecret: "test_client_secret",
            tenantName: "test",
            environment: { base: server.baseUrl, wss: server.baseUrl, login: server.baseUrl, agents: server.baseUrl },
        });
        const rawRequestBody = { client_id: "client_id_123" };
        const rawResponseBody = {
            access_token: "access_token",
            token_type: "token_type",
            expires_in: 1,
            refresh_token: "refresh_token",
            refresh_expires_in: 1,
        };
        server
            .mockEndpoint()
            .post("/base/protocol/openid-connect/token")
            .formUrlEncodedBody(rawRequestBody)
            .respondWith()
            .statusCode(200)
            .jsonBody(rawResponseBody)
            .build();

        const response = await client.auth.getToken({
            tenantName: "base",
            clientId: "client_id_123",
        });
        expect(response).toEqual({
            accessToken: "access_token",
            tokenType: "token_type",
            expiresIn: 1,
            refreshToken: "refresh_token",
            refreshExpiresIn: 1,
        });
    });
});
